public class IterationBidhan {
		// TODO Auto-generated method stub
		public static void main(String[] args) {
	        int base = 2;
	        int exponent = 8;

	        int result = 1;

	        for (int i = 0; i < exponent; i++) {
	            result *= base;
	        }
	        System.out.println("Iteration for exponential is: " + result);
	}
}
